        languages.add(Language("Python","https://www.python.org/static/opengraph-icon-200x200.png"))
        languages.add(Language("Bootstrap","https://commons.bmstu.wiki/images/b/b8/Bootstrap.png"))
        languages.add(Language("ASP.NET","https://qwertyseo.com/wp-content/uploads/2017/05/67e4dee31014593770dfcd064e9faf24a982881c.png"))
